var longestPalindrome = function (s) {
  let sum = 0;
  let obj = {};
  for (let i = 0; i < s.length; i++) {
    let char = s[i];
    if (!obj[char]) obj[char] = 1;
    else {
      obj[char] = 0;
      sum += 2;
    }
  }
  if (sum < s.length) return sum + 1;
  return sum;
};

